import React, { useState, useEffect } from'react';
import './App.css';

function App() {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('https://wbcraper.onrender.com/wb/parse/');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        const products = data.reduce((acc, curr) => acc.concat(curr.Ret), []);
        setData(products);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  if (loading) return <div>Загрузка...</div>;
  if (error) return <div>Ошибка: {error}</div>;

  return (
    <div className="app">
      <h1>Список товаров</h1>
      <div className="card-container">
        {data.map((item, index) => (
          <div key={index} className="card">
            <img src={item.UrlImg} alt={item.Name} />
            <h2>{item.Name}</h2>
            <p>Цена: {item.Price}</p>
            <p>Баллы: {item.Points}</p>
            <a href={item.Url} target="_blank" rel="noopener noreferrer">Перейти на страницу товара</a>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
